              <form onSubmit={this.handleSubmit} className="comment__input-container">
                <label htmlFor="comment" className="comment__input-label">JOIN THE CONVERSATION</label>
                <textarea onChange={this.handleChange} className="comment__text-container" name="comment" rows="10" cols="50" placeholder="Add a new comment"></textarea>
                <button type="submit" className="comment__button">COMMENT</button>
              </form>
            </div>
          </section>
    )
  }
}


