import React from 'react';
import axios from 'axios';
//import Card from '../Card/Card'


class Aside extends React.Component {
  state = {
    cards: []
  }

  componentDidMount() {
    axios.get('/data.json')
    .then(res =>  {

      this.setState({cards: res.data})})
  }

  render() {
    return (
      <div>
        { this.state.cards.map(card => <img src={card.image}/>)}
      </div>
    )
  }
}


export default Aside;
