import React from 'react';
import axios from 'axios'
import NewForm from '../../components/NewForm/NewForm'
import NewList from '../../components/NewList/NewList'

class New extends React.Component {
  state = {
    comments: []
  }
  toggleDone = (id) => {
    // console.log("clicked", id)
    // const todoToToggle = this.state.todos.find(item => id === item.id)
    // this.setState({todos: [...this.state.todos].splice( ,1)})
    this.setState({
      comments: this.state.comments.map(newComment => {
        return newComment.id === id ? {...newComment, done: !newComment.done} : newComment
      })
    })
  }
  handleSubmit = (event) => {
    event.preventDefault();
    this.setState({comments: [...this.state.comments, {
        id: this.state.comments.length+1,
        comment: event.target.newTask.value,
        done: false //put name here
      }]})
    event.target.reset();
  }
  componentDidMount() {
    axios.get('/data.json')
    .then(res => this.setState({comments: res.data}))
  }
  render() {
    return (
      <div className="App">
        <NewForm handleSubmit={this.handleSubmit}/>
        <NewList tasks={this.state.comments} toggleDone={this.toggleDone}/>
      </div>
    );
  }
}

export default New;
