import React from 'react';
import axios from 'axios'
import NewForm from '../../components/NewForm/NewForm'
import NewList from '../../components/NewList/NewList'

class New extends React.Component {
  state = {
    todos: []
  }
  toggleDone = (id) => {
    // console.log("clicked", id)
    // const todoToToggle = this.state.todos.find(item => id === item.id)
    // this.setState({todos: [...this.state.todos].splice( ,1)})
    this.setState({
      todos: this.state.todos.map(todoVal => {
        return todoVal.id === id ? {...todoVal, done: !todoVal.done} : todoVal
      })
    })
  }
  handleSubmit = (event) => {
    event.preventDefault();
    this.setState({todos: [...this.state.todos, {
        id: this.state.todos.length+1,
        description: event.target.newTask.value,
        done: false
      }]})
    event.target.reset();
  }
  componentDidMount() {
    console.log('bang')
    axios.get('/data.json').then(
      res => this.setState({todos: res.data.name})
    )
  }
  render() {
    return (
      <div className="App">
        <NewForm handleSubmit={this.handleSubmit}/>
        <NewList tasks={this.state.todos} toggleDone={this.toggleDone}/>
      </div>
    );
  }
}

export default New;
