import React from 'react';
import userIcon from '../../assets/Images/Mohan-muruge.jpg';


class CommentsInput extends React.Component {
  state = {
    comment: '',
  }

  handleChange = event => {
    this.setState({ comment: event.target.value });
  }

  handleSubmit = event => {
    event.preventDefault();



    this.setState({comment: [...this.state.comment, {
      id: this.state.comment.length+1,
      comment: event.target.comment.value
    }]})
    console.log('final data is', event.target.comment.value)
    event.target.reset();
  }




  render() {
    return (
    <section className="comment">
      <h3 className="comment__counter">3 Comments</h3>
      <div className="comment__form-container">
        <img src={userIcon} alt="User Icon" className="comment__icon"/>
        <form onSubmit={this.handleSubmit} className="comment__input-container">
          <label htmlFor="comment" className="comment__input-label">JOIN THE CONVERSATION</label>
          <textarea onChange={this.handleChange} className="comment__text-container" name="comment" rows="10" cols="50" placeholder="Add a new comment"></textarea>
          <button type="submit" className="comment__button">COMMENT</button>
        </form>
      </div>
    </section>
    )
  }
}


export default CommentsInput;
