import React from 'react';
import '../../App.scss';
import '../Comments/Comments.scss';


function Comments() {
  return (
    <section className="comment">
      <h3 className="comment__counter">3 Comments</h3>
      <div className="comment__form-container">
        <form className="comment__input-container">
          <img className="comment__image" src="" alt="" />
          <label htmlFor="name" className="comment__input-label">NAME</label>
          <input type="text" className="comment__text-container comment__text-container-name" name="name" placeholder="Enter your name"></input>
          <label htmlFor="comment" className="comment__input-label">COMMENT</label>
          <textarea className="comment__text-container comment__text-container-comment" name="comment" rows="10" cols="50" placeholder="Add a new comment"></textarea>
          <button type="submit" className="comment__button">COMMENT</button>
        </form>
      </div>
    </section>
  );
}


export default Comments;
