class Comments extends React.Component {
  state = {
    userName: [],
    userComment: []
  }

  componentDidMount() {
    axios.get('/data.json')
      .then(res => {
        const userName = res.data;
        const userComment = res.data;
        this.setState({ userName, userComment });
      })
  }

  render() {
    return (
      <div className="comment__display">
        { this.state.userName.map(user => <h3>{user.name}</h3>)}

      </div>
    )
  }
}



export default Comments;


